    }

    function displayContent() {
        return channelList.map((channel, i) => {
            let activeChannel_cpy = [...activeChannel];
            if (channel.label === currentChannel) {
                activeChannel_cpy[i] = true;
                setActiveChannel(activeChannel_cpy);
                return <div key={i}>{channel.content}</div>
            } else {
                activeChannel_cpy[i] = false;
                setActiveChannel(activeChannel_cpy);
                return null;
            }
        })
    }

    return(
        <div>
            <div className="channels">
                {
                    channelList.map((channel, i) => (
                        <button
                            key={i}
                            onClick={() => setCurrentChannel(channel.label)}
                            className={(channel.label === currentChannel) ? 'btn channel active' : 'btn channel'}>
                                {channel.label}
                        </button>
                    ))
