import React, { useState } from 'react';
import logo from '../images/Shiroctogone.png';
import title from '../images/title.png';

function Modal(props) {
    var nameList = [
        "Jack the Reaper",
        "Bobby",
        "John Rambo",
        "SpongeBob SquarePants",
        "Kitty the Cat",
        "Pr Heisenberg",
        "Steve",
        "Dr Who",
        "Sephiroth",
        "Shrek",
        "Lupin the 3rd",
        "Ash Ketchum",
        "ALucard",
        "James Bond",
        "Light Yagami",
        "PewDiePie",
        "Link",
        "Dark Sasuke"
    ];

    var random = Math.floor(Math.random() * nameList.length);
    var name = nameList[random];

    const [userName, setUserName] = useState(name);
    // const [channel, setChannel] = useState(1);

    if (userName === "") {
        setUserName(name);
    }

    return (
        <div className="cmodal background-animated">
            <div className="brand">
                <div className="imgContainer">
                    <img src={title} className="title" alt="Title"></img>
                    <img src={logo} className="logo" alt="Logo"></img>
                </div>
                <h2 className="desc">The quick chat app !</h2>
            </div>
            <div className="buttons">
            <div className="wonderful btn btn-default disabled">&#x21bb;</div>
                    <button className="btn join" onClick={event => props.joinAs(userName, "general")}>
                        Join the chat as
                        <p className="username">{userName}</p>
                    </button>
                <button className="btn reload" onClick={event => {setUserName(nameList[Math.floor(Math.random() * nameList.length)])}}>&#x21bb;</button>
            </div>
            <h3 className="or">- or -</h3>
            <div className="log">
                <h3 className="inputDesc">Enter your username</h3>
                <input type="text" className="input" placeholder="Put your Username here" maxLength="23" onChange={event => setUserName(event.target.value)}></input>
            </div>
        </div>
    );
}

export default Modal;
