import React from 'react';

function Content(props) {
    let socket = props.soc
    return props.messages.map((msg) => {
        {
            var infoType = "serv";

            let messageType;
            if (msg.userId) {
                if (msg.userId === socket.id) {
                    messageType = "message self";
                } else {
                    messageType = "message other";
                }
                return (
                    <div className={messageType}>
                        <div className="card">
                            <div className="card-header">{msg.userName}</div>
                            <div className="card-body">{msg.content}</div>
                            <div className="card-footer">Posted at time</div>
                        </div>
                    </div>
                )
            } else {
                messageType = "info";
                return (
                    <div className={messageType}>
                        <div className={infoType}>
                            {msg.content}
                        </div>
                    </div>
                )
            }
        }
    })
}

export default Content;
