import { get } from 'jquery';
import React from 'react';

var start = Date.now();

function Content(props) {
    let socket = props.soc

    function getTimestamp() {
        const options = {
            timeZone:"Etc/GMT-1",
            hour12 : false,
            hour:  "numeric",
            minute: "numeric",seconds:"numeric"
        }

        return new Date().toLocaleTimeString("fr-FR",options)
    }

    function msgToBlock() {
        if (index > 0 && msg.userId != props.messages[index-1].userId) {
            var content = (
                <div>
                    <div className="msg-header">{index} {msg.userName}</div>
                    <div className="msg-body">{msg.content}</div>
                </div>
            )
        }

        if (index > 0 && msg.userId === props.messages[index-1].userId) {
            var content = (
                <div>
                    <div className="msg-body">{msg.content}</div>
                </div>
            )
        }

        if (index < props.messages.length-1 && msg.userId != props.messages[index+1].userId) {
            var content = (
                <div>
                    <div className="msg-body">{msg.content}</div>
                    <div className="msg-footer">Posted at {getTimestamp()}</div>
                </div>
            )
        }

        let messageType;
        if (msg.userId) {
            if (msg.userId === socket.id) {
                messageType = "message self";
            } else {
                messageType = "message other";
            }
            return (
                <div className={messageType}>
                    <div>
                        {content}
                    </div>
                </div>
            )
        } else {
            messageType = "info";
            let infoType = "serv";
            return (
                <div className={messageType}>
                    <div className={infoType}>
                        {msg.content}
                    </div>
                </div>
            )
        }
    }

    return (
        props.messages.map(function(msg,index) {
            (
                <div className="msg">
                    {msgToBlock()};
                </div>
            )
        })
    )
}

export default Content;
