import { get } from 'jquery';
import React from 'react';

function Content(props) {
    let socket = props.soc

    function getTimestamp() {
        const options = {
            timeZone:"Etc/GMT-1",
            hour12 : false,
            hour:  "numeric",
            minute: "numeric",seconds:"numeric"
        }

        return new Date().toLocaleTimeString("fr-FR",options)
    }



    return props.messages.map(function(msg,index) {
        {
            // if (index > 0 && msg.userId != props.messages[index-1].userId) {
            //     var content = (
            //         <div>
            //             <div className="msg-header">{msg.userName}</div>
            //             <div className="msg-body">{msg.content}</div>
            //         </div>
            //     )
            // } else if (index > 0 && msg.userId === props.messages[index-1].userId) {
            //     var content = (
            //         <div>
            //             <div className="msg-body">{msg.content}</div>
            //         </div>
            //     )
            // } else if (index < props.messages.length-1 && msg.userId != props.messages[index+1].userId) {
            //     var content = (
            //         <div>
            //             <div className="msg-body">{msg.content}</div>
            //             <div className="msg-footer">Posted at {getTimestamp()}</div>
            //         </div>
            //     )
            // } else if (index == props.messages.length-1) {
            //     var content = (
            //         <div>
            //             <div className="msg-body">{msg.content}</div>
            //             <div className="msg-footer">Posted at {getTimestamp()}</div>
            //         </div>
            //     )
            // }

            let messageType;
            if (msg.userId) {
                if (msg.userId === socket.id) {
                    messageType = "message self";
                } else {
                    messageType = "message other";
                }
                return (
                    <div className={messageType}>
                        <div>
                            <div className="msg-header">{msg.userName}</div>
                            <div className="msg-body">{msg.content}</div>
                            <div className="msg-footer">Posted at {getTimestamp()}</div>
                        </div>
                    </div>
                )
            } else {
                messageType = "info";
                let infoType = "serv";
                return (
                    <div className={messageType}>
                        <div className={infoType}>
                            {msg.content}
                        </div>
                    </div>
                )
            }
        }
    })
}

export default Content;
