import React, { useState } from 'react';

function Modal(props) {
    const [userName, setUserName] = useState("");
    // const [channel, setChannel] = useState(1);

    return(
        <div>
            <input type="text" className="inputText" placeholder="Put your Username here" onChange={event => setUserName(event.target.value)}></input>
            <button className="btn join" onClick={event => props.joinAs(userName)}>
                Join the chat as {userName}
            </button>
        </div>
    );
}

export default Modal;
