import React, { useState } from 'react';
import Content from './Content';
import $ from 'jquery';

function Body() {
    const [lastMsg, setMsg] = useState("");
    const [history, setHistory] = useState([]);

    function sendMsg(msg) {
        setMsg(msg);
        setHistory([...history, lastMsg]);
    }

    return(
        <div className="frame">
            <div className="content">
                <Content message={history}/>
            </div>
            <div className="textBar">
                <input type="text" className="inputText" placeholder="Write your text here !" onChange={event => sendMsg(event.target.value)}></input>
                <button className="send btn" onClick={event => sendMsg(event.target.value)}>Send</button>
            </div>
        </div>
    );
}

export default Body;
