import React from 'react';

function Content(props) {
    let socket = props.soc
    return props.messages.map((msg) => {
        if (msg.userId === socket.id) {
            return(
                <div className="message self">
                    <div className="card">
                        <div className="card-header">{msg.userName}</div>
                        <div className="card-body">{msg.content}</div>
                        <div className="card-footer">Posted at time</div>
                    </div>
                </div>
            );
        } else if (msg.userId){
            return(
                <div className="message other">
                    <div className="card">
                        <div className="card-header">{msg.userName}</div>
                        <div className="card-body">{msg.content}</div>
                        <div className="card-footer">Posted at time</div>
                    </div>
                </div>
            );
        } else {
            switch (msg.type) {
                case "Connect":
                    return (
                        <div className="info">
                            <div className="self">
                                {msg.content}
                            </div>
                        </div>
                    )

                case "Join":
                    return (
                        <div className="info">
                            <div className="other">
                                <div>{msg.content}</div>
                            </div>
                        </div>
                    )
                    break;

                default:
                    return(
                        <div className="info">
                            <div className="info serv">
                                <div><span>Server</span> - {msg.content}</div>
                            </div>
                        </div>
                    );
                    break;
            }
        }
    })
}

export default Content;
