import React, { useState } from 'react';
import logo from '../Shiroctogone.png';

function Modal(props) {
    var nameList = [
        "Jack the Reaper",
        "Bobby",
        "John Rambo",
        "Dark Sasuke"
    ];

    var random = Math.floor(Math.random()*nameList.length);
    var name = nameList[random];

    const [userName, setUserName] = useState(name);
    // const [channel, setChannel] = useState(1);

    if (userName === "") {
        setUserName(userName + name);
    }

    return(
        <div className="modal">
            <div className="brand">
                <img src={logo} alt="Logo"></img>
                <h1 className="title">Shiro</h1>
                <h2 className="desc">The quick chat app !</h2>
            </div>
            <div className="join">
                <h3 className="inputDesc">Enter your username</h3>
                <input type="text" className="input" placeholder="Put your Username here" onChange={event => setUserName(event.target.value)}></input>
                <button className="btn" onClick={event => props.joinAs(userName, "general")}>
                    Join the chat as <p className="username">{userName}</p>
                </button>
            </div>
        </div>
    );
}

export default Modal;
