import React, { useState } from 'react';
import Body from './Body';

function Header(props) {
    let socket = props.soc;

    const [currentChannel, setCurrentChannel] = useState('Channel 1');


    var channelList = [
        {
            label: 'Channel 1',
            content: (
                <div className="channel-content">
                    <Body soc={socket} active={false}/>
                </div>
            )
        },
        {
            label: 'Channel 2',
            content: (
                <div className="channel-content">
                    <p>channel 2 content</p>
                </div>
            )
        }
    ];

    function addChannel(label) {
        var newChannel = {
            label: label,
            content: (
                <div className="channel-content">
                    <p>{label}</p>
                </div>
            )
        };

        channelList = [...channelList, newChannel];
    }

    function uodateChannel() {

    }

    function removeChannel() {

    }

    return(
        <div>
            <div className="channels">
                {
                    channelList.map((channel, i) => (
                        <button
                            key={i}
                            onClick={() => setCurrentChannel(channel.label)}
                            className={(channel.label === currentChannel) ? 'btn channel active' : 'btn channel'}>
                                {channel.label}
                        </button>
                    ))
                }
                <button className="btn add channel" onClick={addChannel("Chan oni")}>
                    +
                </button>
            </div>

            {
                channelList.map((channel, i) => {
                    if (channel.label === currentChannel) {
                        return <div key={i}>{channel.content}</div>
                    } else {
                        return null;
                    }
                })
            }
        </div>
    );
}

export default Header;
