function msgDisplay(){
    var element = document.getElementsByClassName('inputText');
    var msg = element.value;

    console.log(msg);
}

function clickMsg() {
    var element = document.getElementsByClassName('inputText');
    var msg = element.value;
    var target = document.getElementsByClassName('Content');

    target.insertAfter(msg, target);
}

function Body() {
    return(
        <div className="frame">
            <div className="content">
                Context
            </div>
            <div className="textBar">
                <input type="text" className="inputText" placeholder="Write your text here !" onChange={msgDisplay()}></input>
                <button className="send btn" onClick={clickMsg}>Send</button>
            </div>
        </div>
    );
}

export default Body;
