import React, { useState } from 'react';

function msgDisplay(){
    var element = document.getElementsByClassName('inputText');
    var msg = element.value;

    console.log(msg);
}

function clickMsg() {
    var element = document.getElementsByClassName('inputText');
    var msg = element.value;
    var target = document.getElementsByClassName('Content');

    target.after(msg, target);
}

function Body() {
    const [lastMsg, setMsg] = useState("");
    const [history, setHistory] = useState([]);

    function sendMsg(msg) {
        setHistory([...history, lastMsg]);
        setMsg(msg);

        var target = document.getElementsByClassName('Content');
        target.insertAfter(msg, target);
    }

    function displayMsg() {
        //.after()
    }

    return(
        <div className="frame">
            <div className="content">
                Context
            </div>
            <div className="textBar">
                <input type="text" className="inputText" placeholder="Write your text here !" onChange={event => sendMsg(event.target.value)}></input>
                <button className="send btn" onClick={event => sendMsg(event.target.value)}>Send</button>
            </div>
        </div>
    );
}

export default Body;
