import React, { useState,useEffect } from 'react';
import Content from './Content';
import $ from 'jquery';

function Body(props) {
    const [msg, setMsg] = useState("");
    const [history, setHistory] = useState([{content: "Start of the history."}]);
    const [userInput, setInput] = useState();
    const [firstLaunch, setFirst] = useState(false);
    const socket = props.soc


    useEffect(() => {
        console.log("useEffect triggered");
        if (!firstLaunch) {
            socket.on("update", (message) =>{

                let messageObj = {
                    userId: null,
                    content: message
                }
                displayUpdate(messageObj)
            })

            socket.on("chat", (msgObj) => {
                console.log("Here's the message Obj:",msgObj);
                setHistory(historyCpy => ([...historyCpy, msgObj]))
            })

            setFirst(true)
        }
        function displayUpdate(msgObj){
            setHistory(historyCpy => ([...historyCpy, msgObj]))
        }


    }, [history])

    function sendMsg() {
        if (msg !== "") {
            console.log("SendMsg:",history);
            let messageObj = {
                userId: socket.id,
                content: msg
            }
            socket.emit("post", messageObj)
            setMsg("")
            userInput.focus()
        }
    }

    function checkEnter(event) {
        if (event.key === 'Enter') {
            sendMsg();
        } else {

        }
    }

    return(
        <div className="frame">
            <div className="content">
                <Content messages={history} soc={socket}/>
            </div>
            <div className="textBar">
                <input type="text" ref={(input) => {setInput(input)}} className="inputText" placeholder="Write your text here !" value={msg} onKeyPress={event => checkEnter(event)} onChange={event => setMsg(event.target.value)}></input>
                <button className="send btn" onClick={event => sendMsg(msg)}>Send</button>
            </div>
        </div>
    );
}

export default Body;
