import React from 'react';

function Content(props) {
    let socket = props.soc
    return props.messages.map((msg) => {
        if (msg.userId === socket.id) {
            return(
                <div className="message self">
                    <div className="card">
                        <div className="card-header">{msg.userName}</div>
                        <div className="card-body">{msg.content}</div>
                        <div className="card-footer">Posted at time</div>
                    </div>
                </div>
            );
        }else if (msg.userId){
            return(
                <div className="message other">
                    <div className="card">
                        <div className="card-header">{msg.userName}</div>
                        <div className="card-body">{msg.content}</div>
                        <div className="card-footer">Posted at time</div>
                    </div>
                </div>
            );
        }else{
            return(
                <div>
                    <div className="servinfo">
                        <div><span>Server</span> - {msg.content}</div>
                    </div>
                </div>
            );
        }
    })
}

export default Content;
