import React, { useState } from 'react';

function Header() {

    const [currentChannel, setCurrentChannel] = useState('tab1');
    const channelList = [
        {
            name: 'channel1',
            label: 'Channel 1',
            content: (
                <div className="tab-content">
                    <p>channel 1 content</p>
                </div>
            )
        },
        {
            name: 'channel2',
            label: 'Channel 2',
            content: (
                <div className="tab-content">
                    <p>channel 2 content</p>
                </div>
            )
        }
    ];

    return(
        <div>
            <div className="nav-items">
                {
                    channelList.map((channel, i) => (
                        <button
                            key={i}
                            onClick={() => setCurrentChannel(channel.name)}
                            className={(channel.name === currentChannel) ? 'nav-link active' : 'nav-link'}>
                                {channel.label}
                        </button>
                    ))
                }
            </div>

            {
                channelList.map((channel, i) => {
                    if (channel.name === currentChannel) {
                        return <div key={i}>{channel.content}</div>
                    } else {
                        return null;
                    }
                })
            }
        </div>
    );
}

export default Header;
