import React, { useState } from 'react';

function Modal(props) {
    var nameList = [
        "Jack the Reaper",
        "Bobby",
        "John Rambo",
        "Dark Sasuke"
    ];
    
    var random = Math.floor(Math.random()*nameList.length);
    var name = nameList[random];

    const [userName, setUserName] = useState(name);
    // const [channel, setChannel] = useState(1);

    if (userName === "") {
        setUserName(userName + name);
    }

    return(
        <div className="modal">
            <h1 className="modalTitle">Shiro</h1>
            <h3 className="modalText">Enter your username</h3>
            <input type="text" className="modalInput" placeholder="Put your Username here" onChange={event => setUserName(event.target.value)}></input>
            <button className="btn modalJoin" onClick={event => props.joinAs(userName)}>
                Join the chat as <p className="username">{userName}</p>
            </button>
        </div>
    );
}

export default Modal;
