import React, { useState } from 'react';

function Header() {

    const [currentChannel, setCurrentChannel] = useState(tab1);
    const channelList = [
        {
            name: 'channel1',
            label: 'Channel 1',
            content: (
                <div className="tab-content">
                    <p>channel 1 content</p>
                </div>
            )
        },
        {
            name: 'channel2',
            label: 'Channel 2',
            content: (
                <div className="tab-content">
                    <p>channel 2 content</p>
                </div>
            )
        }
    ];

    return(
        <div>
            {/* Channels buttons */}
            <div className="Channels">
                {
                    channelList.map((channel, i) => (
                        <button
                            key={i}
                            onClick={() => setCurrentChannel(channel.name)}
                            className={(channel.name === currentChannel)}>
                                {channel.label}
                        </button>
                    ))
                }
            </div>

            {/* Channels content */}
            <div className="tab-content">
                <div className="tab-pane container active" id="chan1"><p className="text-white">chan1</p></div>
                <div className="tab-pane container fade" id="chan2"><p className="text-white">chan2</p></div>
                <div className="tab-pane container fade" id="chan3"><p className="text-white">chan3</p></div>
            </div>
        </div>
    );
}

export default Header;
