import React, { useState } from 'react';

function msgDisplay(){
    var element = document.getElementsByClassName('inputText');
    var msg = element.value;

    console.log(msg);
}

function clickMsg() {
    var element = document.getElementsByClassName('inputText');
    var msg = element.value;
    var target = document.getElementsByClassName('Content');

    target.after(msg, target);
}

function Body() {
    var element = document.getElementsByClassName('inputText');
    var msg = element.value;

    var [lastMsg, updateMsg] = useState("");

    return(
        <div className="frame">
            <div className="content">
                Context
            </div>
            <div className="textBar">
                <input type="text" className="inputText" placeholder="Write your text here !" onChange={msgDisplay()}></input>
                <button className="send btn" onClick={() => updateMsg()}>Send</button>
            </div>
        </div>
    );
}

export default Body;
