import { get } from 'jquery';
import React from 'react';

function Content(props) {
    let socket = props.soc

    function msgStacker() {
        let blockArr= [[]];
        let indexBlock = 0;
        props.messages.map(function(msg,index) {
            if ((index > 0 && msg.sender !== props.messages[index-1].sender)){
                blockArr = [...blockArr, [msg]];
                indexBlock++
            } else{
                blockArr[indexBlock] = [...blockArr[indexBlock], msg];
            }
        })

        return blockArr;
    }

    function msgUserBuilder(msg,index,length) {
        if (index === 0) {
            if (length === 1) {
                return (
                    <div>
                        <div className="d-flex flex-row msg-header">
                            <div className="msg-user">{props.users.content[msg.sender]}</div>
                            <div className="msg-role">/ Admin, Modérateur</div>
                        </div>
                        <div className="msg-body">{msg.content}</div>
                        <div className="msg-footer">Posted at {msg.timeStamp}</div>
                    </div>
                )
            } else {
                return (
                    <div>
                        <div className="d-flex flex-row msg-header">
                            <div className="msg-user">{props.users.content[msg.sender]}</div>
                            <div className="msg-role">/ Admin, Modérateur</div>
                        </div>
                        <div className="msg-body">{msg.content}</div>
                    </div>
                )
            }
        } else if (index === length-1) {
            return (
                <div>
                    <div className="msg-body">{msg.content}</div>
                    <div className="msg-footer">Posted at {msg.timeStamp}</div>
                </div>
            )
        } else {
            return (
                <div className="msg-body">{msg.content}</div>
            )
        }
    }

    function msgServerBuilder(msg,index,length) {
        if (index === 0) {
            return (
                <div>
                    <div className="d-flex flex-row msg-header">
                        <div className="msg-user">{msg.sender}</div>
                        <div className="msg-role">/ Super Admin</div>
                    </div>
                    <div className="serv-body">{msg.timeStamp} - {msg.content}</div>
                </div>
            )
        } else {
            return (
                <div>
                    <div className="serv-body">{msg.timeStamp} - {msg.content}</div>
                </div>
            )
        }
    }

    function msgTypeChecker(msgArr) {
        return msgArr.map(function(msg, index) {
            let messageType;
            console.log("msg sender",msg.sender);
            if (msg.sender !== 'server') {
                console.log(msg.sender);
                if (msg.sender === socket.id) {
                    messageType = "message self";
                } else {
                    messageType = "message other";
                }
                return (
                    <div className={messageType}>
                            {msgUserBuilder(msg, index, msgArr.length)}
                    </div>
                )
            } else {
                messageType = "info";
                let infoType = "serv "+msg.type;
                return (
                    <div className={messageType}>
                        <div className={infoType}>
                            {msgServerBuilder(msg, index, msgArr.length)}
                        </div>
                    </div>
                )
            }
        })
    }

    return msgStacker().map(function(msgArr) {
        return (
        <div className="msg">
            {msgTypeChecker(msgArr)}
        </div>
        )
    })
}

export default Content;
