import { get } from 'jquery';
import React from 'react';

function Content(props) {
    let socket = props.soc

    return props.messages.map(function(msg,index) {
        //Créer un array d'array pour séparer chaque block de message
        let msgArr = [];
        let blockArr= [];

        props.messages.map(function(msg,index) {
            if (index > 0 && msg.sender != props.messages[index-1].sender) {
                blockArr = [...blockArr, msgArr];
                msgArr = [];
            } else {
                msgArr = [...msgArr, msg];
            }
        })

        console.log("Block",blockArr);
        //Construction du block en jsx

        // eslint-disable-next-line no-lone-blocks
        {
            // if (index > 0 && msg.sender != props.messages[index-1].sender) {
            //     var content = (
            //         <div>
            //             <div className="msg-header">{msg.userName}</div>
            //             <div className="msg-body">{msg.content}</div>
            //         </div>
            //     )
            // } else if (index > 0 && msg.sender === props.messages[index-1].sender) {
            //     var content = (
            //         <div>
            //             <div className="msg-body">{msg.content}</div>
            //         </div>
            //     )
            // } else if (index < props.messages.length-1 && msg.sender != props.messages[index+1].sender) {
            //     var content = (
            //         <div>
            //             <div className="msg-body">{msg.content}</div>
            //             <div className="msg-footer">Posted at {getTimestamp()}</div>
            //         </div>
            //     )
            // } else if (index == props.messages.length-1) {
            //     var content = (
            //         <div>
            //             <div className="msg-body">{msg.content}</div>
            //             <div className="msg-footer">Posted at {getTimestamp()}</div>
            //         </div>
            //     )
            // }

            let messageType;
            if (msg.sender !== 'server') {
                console.log(msg.sender);
                if (msg.sender === socket.id) {
                    messageType = "message self";
                } else {
                    messageType = "message other";
                }
                return (
                    <div className={messageType}>
                        <div className="msg">
                            <div className="d-flex flex-row msg-header">
                                <div className="msg-user">{props.users.content[msg.sender]}</div>
                                <div className="msg-role">/ Admin, Modérateur</div>
                            </div>
                            <div className="msg-body">{msg.content}</div>
                            <div className="msg-footer">Posted at {msg.timeStamp}</div>
                        </div>
                    </div>
                )
            } else {
                messageType = "info";
                let infoType = "serv "+msg.type;
                return (
                    <div className={messageType}>
                        <div className={infoType}>
                            {msg.timeStamp} - {msg.content}
                        </div>
                    </div>
                )
            }
        }
    })
}

export default Content;
